"""@Author: Shine_Light@Version: 1.0@Date: 2022/4/21 13:20"""import jsonimport requestsimport os# 路径处理dir_path = os.path.dirname(os.path.abspath(__file__))dir_path = dir_path.split("\\")dir_path.pop(-1)dir = ""for a in dir_path:    dir += a + "/"dir = dir.replace(f"content/plugins/update/version/", "")# 翻译文件更新with open(dir + "config/" + "translate.json", "w+", encoding="utf-8") as file:    file.write(requests.get("http://cdn.shinelight.xyz/nonebot/translate.json").text)    file.close()# 权限文件更新for f in os.listdir(dir + "config/" + "permission/" + "common"):    name = f.split(".")[0]    with open(dir + "config/" + "permission/" + "common/" + f"{name}.json", "w+", encoding="utf-8") as file:        file.write(requests.get("http://cdn.shinelight.xyz/nonebot/permission_common.json").text)        file.close()for f in os.listdir(dir + "config/" + "permission/" + "special"):    name = f.split(".")[0]    with open(dir + "config/" + "permission/" + "special/" + f"{name}.json", "w+", encoding="utf-8") as file:        file.write(requests.get("http://cdn.shinelight.xyz/nonebot/permission_special.json").text)        file.close()# 结束